import axios from "axios";

const apiClient= axios.create(
    {
        baseURL: 'http://localhost:8080'
    }
)

export function retrieveHelloWorldBean() {
    return apiClient.get('/hello-world-bean');
}

export function retrieveHelloWorldBeanWithPathVariable(name) {
    return apiClient.get(`/hello-world/path-variable/${name}`);
}

export function execteBasicAuthenticationService(token) {
    return apiClient.get(`basicauth`, {
        headers: {
            Authorization: "Basic aW4yOG1pbnV0ZXM6ZHVtbXk="
        }
    });
}
