import { apiClient } from "./ApiClient";


export function retrieveAllTodosForUsername(username, token) {
    return apiClient.get(`/users/${username}/todos`,{
        headers: {
            Authorization: token
        }
    });
}


export function deleteTodoApi(username, id, token) {
    return apiClient.delete(`/users/${username}/todos/${id}`,{
        headers: {
            Authorization: token
        }
    });
}

export function retrieveTodoApi(username, id, token) {
    return apiClient.get(`/users/${username}/todos/${id}`,{
        headers: {
            Authorization: token
        }
    });
}

export function updateTodoApi(username, id, todo, token) {
    return apiClient.put(`/users/${username}/todos/${id}`, todo,{
        headers: {
            Authorization: token
        }
    });
}

export function createTodoApi(username, todo, token) {
    return apiClient.post(`/users/${username}/todos`, todo,{
        headers: {
            Authorization: token
        }
    });
}
