import {useState} from "react";
import "./Login.css"

export default function Login() {

	const[username, setUsername] = useState("juamba");
	const[password, setPassword] = useState("");
	const[showSuccessMessage, setShowSuccessMessage] = useState(false);
	const[showFailureMessage, setShowFailureMessage] = useState(false);

	function handleUsernameChange(event) {
		setUsername(event.target.value);
	}

	function handlePasswordChange(event) {
		setPassword(event.target.value);
	}

	function handleSubmit(){
		if (username === "juamba" && password === "123") {
			console.log("Login Successful");
			setShowSuccessMessage(true);
			setShowFailureMessage(false);
		} else {
			setShowSuccessMessage(false);
			setShowFailureMessage(true);
		} 
	}

	function ShowSuccessMessage(){
		if (showSuccessMessage) {
			return <div className="successMessage">Login Successful</div>
		} else return null

	}

	function ShowFailureMessage(){
		if (showFailureMessage) {
			return <div className="failureMessage">Invalid Credentials</div>
		} else return null
	}

	return (
		<div className="login">
			<ShowSuccessMessage/>
			<ShowFailureMessage/>
			<div className="LoginForm">
				<div>
					<label>User Name</label>
					<input type="text" name="username" value={username} onChange={handleUsernameChange}/>
				</div>
				<div>
					<label>Password</label>
					<input type="password" name="password" value={password} onChange={handlePasswordChange}/>
				</div>
				<button type="button" value="Login" onClick={handleSubmit}>
					login
				</button>
			</div>
		</div>
	);
}
